package com.Pagina_Eventos.Pagina_Eventos.Entidad;

import jakarta.persistence.*;
import lombok.AllArgsConstructor;
import lombok.Data;
import lombok.NoArgsConstructor;

@Entity
@Table(name = "lista_boleto", uniqueConstraints = {
    @UniqueConstraint(columnNames = {"id_ubicacion", "id_boleto"})
})
@Data
@NoArgsConstructor
@AllArgsConstructor
public class ListaBoleto {
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Integer id;

    @ManyToOne
    @JoinColumn(name = "id_ubicacion", nullable = false)
    private Ubicacion ubicacion;

    @ManyToOne
    @JoinColumn(name = "id_boleto", nullable = false)
    private Boleto boleto;
}

package com.Pagina_Eventos.Pagina_Eventos.Entidad;

import com.fasterxml.jackson.annotation.JsonIgnoreProperties;
import jakarta.persistence.*;
import lombok.AllArgsConstructor;
import lombok.Data;
import lombok.NoArgsConstructor;

@Entity
@Table(name = "lista_boleto", uniqueConstraints = {
    @UniqueConstraint(columnNames = {"id_ubicacion", "id_boleto"})
})
@Data
@NoArgsConstructor
@AllArgsConstructor
public class ListaBoleto {
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Integer id;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "id_ubicacion", nullable = false)
    @JsonIgnoreProperties({"hibernateLazyInitializer", "handler"})
    private Ubicacion ubicacion;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "id_boleto", nullable = false)
    @JsonIgnoreProperties({"hibernateLazyInitializer", "handler"})
    private Boleto boleto;
}

package com.Pagina_Eventos.Pagina_Eventos.Entidad;

import jakarta.persistence.*;
import lombok.AllArgsConstructor;
import lombok.Data;
import lombok.NoArgsConstructor;

@Entity
@Table(name = "lista_boletos_pagados")
@Data
@NoArgsConstructor
@AllArgsConstructor
public class ListaBoletosPagados {
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Integer id;

    @ManyToOne
    @JoinColumn(name = "id_boleta_venta", nullable = false)
    private BoletaVenta boletaVenta;

    @ManyToOne
    @JoinColumn(name = "id_boleto", nullable = false)
    private Boleto boleto;

    @ManyToOne
    @JoinColumn(name = "id_usuario", nullable = false)
    private Usuario usuario;

    @Column(nullable = false)
    private Integer cantidad = 1;
}

package com.Pagina_Eventos.Pagina_Eventos.Entidad;

import com.fasterxml.jackson.annotation.JsonIgnoreProperties;
import jakarta.persistence.*;
import lombok.AllArgsConstructor;
import lombok.Data;
import lombok.NoArgsConstructor;

@Entity
@Table(name = "lista_boletos_pagados")
@Data
@NoArgsConstructor
@AllArgsConstructor
public class ListaBoletosPagados {
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Integer id;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "id_boleta_venta", nullable = false)
    @JsonIgnoreProperties({"hibernateLazyInitializer", "handler"})
    private BoletaVenta boletaVenta;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "id_boleto", nullable = false)
    @JsonIgnoreProperties({"hibernateLazyInitializer", "handler"})
    private Boleto boleto;

    @ManyToOne(fetch = FetchType.LAZY)
    @JoinColumn(name = "id_usuario", nullable = false)
    @JsonIgnoreProperties({"hibernateLazyInitializer", "handler"})
    private Usuario usuario;

    @Column(nullable = false)
    private Integer cantidad;

    @PrePersist
    protected void onCreate() {
        if (cantidad == null) {
            cantidad = 1;
        }
    }
}
